#include <stdint.h>		// include this to use standardised type, e.g. uint32_t; avoids different implementations of int on different architectures

int main(void)
{
	uint32_t *pClkCtrlReg = (uint32_t*) (0x40023800 + 0x30);
	uint32_t *pPortDModeReg = (uint32_t*) (0x40020c00 + 0x00);
	uint32_t *pPortDOutReg = (uint32_t*) (0x40020c00 + 0x14);

	//1. ENABLE the CLOCK for GPIO D peripheral in AHB1 ENR, by setting bit 3 to "1"
	*pClkCtrlReg |= (1 << 3);
	//*pClkCtrlReg &= !(1 << 3)


	//2. SET the MODE for GPIO D12-D15 peripheral, by setting bits 31,30,29,28,27,26,25,24, to "01"
	// first, clear the bits (set them to 0);
	*pPortDModeReg &= ~(255 << 24);
	// second, need to set bits 31,30,29,28,27,26,25,24 to "01", by sending 85 (b01010101) up 24 places
	*pPortDModeReg |= (85 << 24);


	// 3. SET the OUTPUT DATA for GPIO D12 peripheral, but setting bit 12 to "1"
	//*pPortDOutReg |= (1 << 12);
	//*pPortDOutReg &= ~(1 << 12);		// reset bit 12 to 0


	// 4. LOOP to SPIRAL the led
	uint32_t i = 0;
	while(1){
		if(i % 400000 == 0){
			*pPortDOutReg ^= (1 << 12);
		}
		else if(i % 400000 == 100000){
			*pPortDOutReg ^= (1 << 13);
		}
		else if(i % 400000 == 200000){
			*pPortDOutReg ^= (1 << 14);
		}
		else if(i % 400000 == 300000){
			*pPortDOutReg ^= (1 << 15);
		}
		i++;
	}
	//return 0;
}


